import React from "react";

function TodoList({ todos, markComplete }) {
  const activeTodos = todos.filter((todo) => !todo.completed);
  const completedTodos = todos.filter((todo) => todo.completed);

  // Sort completedTodos by completion date (most recent on top)
  completedTodos.sort((a, b) => b.completedAt - a.completedAt);

  return (
    <div className="todo-list">
      <h2>Active Todos</h2>
      {activeTodos.map((todo, index) => (
        <div
          key={index}
          className={`todo-item ${todo.completed ? "completed" : ""}`}
          onClick={() => !todo.completed && markComplete(index)}
        >
          {todo.text}
        </div>
      ))}
      <h2>Completed Todos</h2>
      {completedTodos.map((todo, index) => (
        <div
          key={index}
          className={`todo-item completed`}
          onClick={() => markComplete(index)}
        >
          {todo.text}
        </div>
      ))}
    </div>
  );
}

export default TodoList;
